import React, { Component } from 'react';
 const ingredients = ['1 1/2 cups milk', '1/2 cup mascarpone', '1/2 tsp pink salt', '1lb Black mission figs', '1/2 cup brown sugar', '2-4 tbsp water', '1 1/2 cups heavy cream','1/3 granulated sugar', '2 egg yolks', '1 lemon', '2 tbsp butter', '1 cup honey roasted pecans']


export default class Recipe extends Component {
    render() {
        return (
            <div>
                <ul className="ingredients">
                    <img src="list-bg.png" alt = 'list-bg'/>
                    {this.props.ingredients.map((ingredient, index) => (
                        <li key= {index}>{this.ingredient}</li>
                    ))}
                </ul>
            </div>
        )
    }

}
